// https://doc.wikimedia.org/mediawiki-core/master/js/source/mediawiki.base.html#mw-html-method-escape
cm.escapeHTML = function(str){
    if (!cm.isString(str)) {
        return;
    }
    function escapeCallback(str){
        switch(str){
            case '\'':
                return '&#039;';
            case '"':
                return '&quot;';
            case '<':
                return '&lt;';
            case '>':
                return '&gt;';
            case '&':
                return '&amp;';
        }
    }

    return str.replace( /['"<>&]/g, escapeCallback );
};

