cm.parseLocalNumber = function(str){
    // Check if comma is a decimal separator
    const hasCommaDecimal = /,\d{1,2}$/.test(str);
    if (hasCommaDecimal) {
        // European format: "2.415,00"
        return parseFloat(str.replace(/\./g, '').replace(',', '.'));
    } else {
        // US format: "2,415.00"
        return parseFloat(str.replace(/,/g, ''));
    }
};

