            'form': that,
            'formName': that.params.name,
            'system': false,
            'name': '',
            'dataName': null,
            'dataPath': null,
            'label': '',
            'originValue': null,
            'required': false,
            'validate': false,
            'send': true,
            'sendEmpty': true,
            'sendAlways': false,
            'sendPath': null,
            'sendCallback': null,
            'preventReset': false,
            'options': [],
            'container': that.nodes.fields,
