        const that = this;

        // Validate
        item = cm.merge({
            i: i,
            icon: 'small',
            iconPosition: 'leftInside',
            name: '',
            nodes: {}
        }, item);

        // Structure
        item.nodes = that.renderInputContainer(item);
        item.input = item.nodes.input;

        // Attributes
        if (that.params.maxlength) {
            item.input.setAttribute('maxlength', that.params.maxlength);
        }

        // Events
